function AddToCart(n,t,i,r){$.ajax({url:"/Cart/AddToCart/"+n+"/"+i+"/"+r,type:"GET",success:function(n){if(n.status==="success"){var i=n.count;$("#cartCounter").text(i);$("#toastCart > .toast-body").text(t+" added to cart successfully!");$("#toastCart").toast("show");setTimeout(function(){$("#toastCart").toast("hide")},4e3)}}})}function UpdateQuantity(n,t,i){(t>=1&&i>0||t>1&&i==-1)&&$.ajax({url:"/Cart/UpdateQuantity/"+n+"/"+i,type:"GET",success:function(n){n>0&&location.reload()}})}function DeleteItem(n){$.ajax({url:"/Cart/DeleteItem/"+n,type:"GET",success:function(n){n>0&&location.reload()}})}function Greet(){alert("Hello from site.js")}$(document).ready(function(){$.ajax({type:"GET",url:"/Cart/GetCartCount",success:function(n){$("#cartCounter").text(n)}})});